import React, { useState } from "react";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

function Form({ formData, setFormData, activeSection }) {
  // handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // handle array changes (skills, projects, etc.)
  const handleArrayChange = (field, index, value) => {
    const newArray = [...formData[field]];
    newArray[index] = value;
    setFormData({ ...formData, [field]: newArray });
  };

  // add new array item
  const handleAddField = (field) => {
    setFormData({ ...formData, [field]: [...formData[field], ""] });
  };

  // remove array item
  const handleRemoveField = (field, index) => {
    const newArray = [...formData[field]];
    newArray.splice(index, 1);
    setFormData({ ...formData, [field]: newArray });
  };

  return (
    <form>
      {/* Personal Info */}
      <h3 style={styles.sectionTitle}>Personal Information</h3>
      <input
        style={styles.input}
        type="text"
        name="name"
        placeholder="Full Name"
        value={formData.name}
        onChange={handleChange}
      />
      <input
        style={styles.input}
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
      />
      <input
        style={styles.input}
        type="text"
        name="phone"
        placeholder="Phone"
        value={formData.phone}
        onChange={handleChange}
      />

      {/* Summary */}
      <h3 style={styles.sectionTitle}>Summary</h3>
      <textarea
        style={styles.textarea}
        name="summary"
        placeholder="Write a short summary about yourself"
        value={formData.summary}
        onChange={handleChange}
      />

      {/* Education */}
      <h3 style={styles.sectionTitle}>Education</h3>
      <input
        style={styles.input}
        type="text"
        name="education"
        placeholder="Education Details"
        value={formData.education}
        onChange={handleChange}
      />

      {/* Skills */}
      <h3 style={styles.sectionTitle}>Skills</h3>
      {formData.skills.map((skill, index) => (
        <div key={index} style={styles.dynamicField}>
          <input
            style={styles.input}
            type="text"
            placeholder="Skill"
            value={skill}
            onChange={(e) => handleArrayChange("skills", index, e.target.value)}
          />
          <button
            type="button"
            style={styles.removeBtn}
            onClick={() => handleRemoveField("skills", index)}
          >
            ✕
          </button>
        </div>
      ))}
      <button
        type="button"
        style={styles.addBtn}
        onClick={() => handleAddField("skills")}
      >
        + Add Skill
      </button>

      {/* Projects */}
      <h3 style={styles.sectionTitle}>Projects</h3>
      {formData.projects.map((project, index) => (
        <div key={index} style={styles.dynamicField}>
          <input
            style={styles.input}
            type="text"
            placeholder="Project"
            value={project}
            onChange={(e) =>
              handleArrayChange("projects", index, e.target.value)
            }
          />
          <button
            type="button"
            style={styles.removeBtn}
            onClick={() => handleRemoveField("projects", index)}
          >
            ✕
          </button>
        </div>
      ))}
      <button
        type="button"
        style={styles.addBtn}
        onClick={() => handleAddField("projects")}
      >
        + Add Project
      </button>

      {/* Extra Sections for Experienced */}
      {activeSection === "experienced" && (
        <>
          <h3 style={styles.sectionTitle}>Experience</h3>
          {formData.experience.map((exp, index) => (
            <div key={index} style={styles.dynamicField}>
              <input
                style={styles.input}
                type="text"
                placeholder="Job Experience"
                value={exp}
                onChange={(e) =>
                  handleArrayChange("experience", index, e.target.value)
                }
              />
              <button
                type="button"
                style={styles.removeBtn}
                onClick={() => handleRemoveField("experience", index)}
              >
                ✕
              </button>
            </div>
          ))}
          <button
            type="button"
            style={styles.addBtn}
            onClick={() => handleAddField("experience")}
          >
            + Add Experience
          </button>

          <h3 style={styles.sectionTitle}>Certifications</h3>
          {formData.certifications.map((cert, index) => (
            <div key={index} style={styles.dynamicField}>
              <input
                style={styles.input}
                type="text"
                placeholder="Certification"
                value={cert}
                onChange={(e) =>
                  handleArrayChange("certifications", index, e.target.value)
                }
              />
              <button
                type="button"
                style={styles.removeBtn}
                onClick={() => handleRemoveField("certifications", index)}
              >
                ✕
              </button>
            </div>
          ))}
          <button
            type="button"
            style={styles.addBtn}
            onClick={() => handleAddField("certifications")}
          >
            + Add Certification
          </button>
        </>
      )}

      {/* Achievements */}
      <h3 style={styles.sectionTitle}>Achievements</h3>
      {formData.achievements.map((ach, index) => (
        <div key={index} style={styles.dynamicField}>
          <input
            style={styles.input}
            type="text"
            placeholder="Achievement"
            value={ach}
            onChange={(e) =>
              handleArrayChange("achievements", index, e.target.value)
            }
          />
          <button
            type="button"
            style={styles.removeBtn}
            onClick={() => handleRemoveField("achievements", index)}
          >
            ✕
          </button>
        </div>
      ))}
      <button
        type="button"
        style={styles.addBtn}
        onClick={() => handleAddField("achievements")}
      >
        + Add Achievement
      </button>
    </form>
  );
}

function ResumePreview({ formData, activeSection }) {
  return (
    <div>
      <h2>{formData.name}</h2>
      <p>
        {formData.email} | {formData.phone}
      </p>
      <h3>Summary</h3>
      <p>{formData.summary}</p>
      <h3>Education</h3>
      <p>{formData.education}</p>
      <h3>Skills</h3>
      <ul>
        {formData.skills.map((skill, i) => (
          <li key={i}>{skill}</li>
        ))}
      </ul>
      <h3>Projects</h3>
      <ul>
        {formData.projects.map((project, i) => (
          <li key={i}>{project}</li>
        ))}
      </ul>
      {activeSection === "experienced" && (
        <>
          <h3>Experience</h3>
          <ul>
            {formData.experience.map((exp, i) => (
              <li key={i}>{exp}</li>
            ))}
          </ul>
          <h3>Certifications</h3>
          <ul>
            {formData.certifications.map((cert, i) => (
              <li key={i}>{cert}</li>
            ))}
          </ul>
        </>
      )}
      <h3>Achievements</h3>
      <ul>
        {formData.achievements.map((ach, i) => (
          <li key={i}>{ach}</li>
        ))}
      </ul>
    </div>
  );
}

export default function App() {
  const [activeSection, setActiveSection] = useState("fresher");
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    summary: "",
    education: "",
    skills: [""],
    projects: [""],
    experience: [""],
    certifications: [""],
    achievements: [""],
  });

  // PDF download
  const downloadPDF = () => {
    const input = document.getElementById("resume-preview");
    html2canvas(input, { scale: 2 }).then((canvas) => {
      const pdf = new jsPDF("p", "mm", "a4");
      const imgData = canvas.toDataURL("image/png");
      const imgWidth = 210;
      const pageHeight = 295;
      const imgHeight = (canvas.height * imgWidth) / canvas.width;
      let heightLeft = imgHeight;
      let position = 0;

      pdf.addImage(imgData, "PNG", 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;

      while (heightLeft >= 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, "PNG", 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
      }

      pdf.save("resume.pdf");
    });
  };

  return (
    <div style={styles.app}>
      {/* Header */}
      <header style={styles.header}>
        <div style={styles.headerContent}>
          <div>
            <h1 style={styles.headerH1}>Resume Builder</h1>
            <p style={styles.headerP}>Create professional resumes with ease</p>
          </div>
          <div style={styles.toggleContainer}>
            <button
              style={{
                ...styles.toggleBtn,
                ...(activeSection === "fresher" ? styles.toggleBtnActive : {}),
              }}
              onClick={() => setActiveSection("fresher")}
            >
              Fresher
            </button>
            <button
              style={{
                ...styles.toggleBtn,
                ...(activeSection === "experienced"
                  ? styles.toggleBtnActive
                  : {}),
              }}
              onClick={() => setActiveSection("experienced")}
            >
              Experienced
            </button>
          </div>
        </div>
      </header>

      {/* Main */}
      <main style={styles.mainContent}>
        <section style={styles.formSectionWrapper}>
          <Form
            formData={formData}
            setFormData={setFormData}
            activeSection={activeSection}
          />
        </section>

        <section style={styles.previewSectionWrapper}>
          <div style={styles.previewHeader}>
            <div>
              <h2 style={styles.previewHeaderH2}>Resume Preview</h2>
              <p style={styles.previewHeaderP}>
                Live preview of your resume
              </p>
            </div>
            <button style={styles.downloadBtn} onClick={downloadPDF}>
              Download PDF
            </button>
          </div>
          <div style={styles.previewContainer} id="resume-preview">
            <ResumePreview
              formData={formData}
              activeSection={activeSection}
            />
          </div>
        </section>
      </main>
    </div>
  );
}

