import React, { useState } from "react";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

function Form({ formData, setFormData, activeSection }) {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleArrayChange = (field, index, value) => {
    const newArray = [...formData[field]];
    newArray[index] = value;
    setFormData({ ...formData, [field]: newArray });
  };

  const handleAddField = (field) => {
    setFormData({ ...formData, [field]: [...formData[field], ""] });
  };

  const handleRemoveField = (field, index) => {
    const newArray = [...formData[field]];
    newArray.splice(index, 1);
    setFormData({ ...formData, [field]: newArray });
  };

  return (
    <form>
      <h3 style={styles.sectionTitle}>Personal Information</h3>
      <input style={styles.input} name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} />
      <input style={styles.input} name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
      <input style={styles.input} name="phone" placeholder="Phone" value={formData.phone} onChange={handleChange} />

      <h3 style={styles.sectionTitle}>Summary</h3>
      <textarea style={styles.textarea} name="summary" placeholder="Write a short summary" value={formData.summary} onChange={handleChange} />

      <h3 style={styles.sectionTitle}>Education</h3>
      <input style={styles.input} name="education" placeholder="Education Details" value={formData.education} onChange={handleChange} />

      <h3 style={styles.sectionTitle}>Skills</h3>
      {formData.skills.map((s, i) => (
        <div key={i} style={styles.dynamicField}>
          <input style={styles.input} placeholder="Skill" value={s} onChange={(e) => handleArrayChange("skills", i, e.target.value)} />
          <button type="button" style={styles.removeBtn} onClick={() => handleRemoveField("skills", i)}>✕</button>
        </div>
      ))}
      <button type="button" style={styles.addBtn} onClick={() => handleAddField("skills")}>+ Add Skill</button>

      <h3 style={styles.sectionTitle}>Projects</h3>
      {formData.projects.map((p, i) => (
        <div key={i} style={styles.dynamicField}>
          <input style={styles.input} placeholder="Project" value={p} onChange={(e) => handleArrayChange("projects", i, e.target.value)} />
          <button type="button" style={styles.removeBtn} onClick={() => handleRemoveField("projects", i)}>✕</button>
        </div>
      ))}
      <button type="button" style={styles.addBtn} onClick={() => handleAddField("projects")}>+ Add Project</button>

      {activeSection === "experienced" && (
        <>
          <h3 style={styles.sectionTitle}>Experience</h3>
          {formData.experience.map((exp, i) => (
            <div key={i} style={styles.dynamicField}>
              <input style={styles.input} placeholder="Job Experience" value={exp} onChange={(e) => handleArrayChange("experience", i, e.target.value)} />
              <button type="button" style={styles.removeBtn} onClick={() => handleRemoveField("experience", i)}>✕</button>
            </div>
          ))}
          <button type="button" style={styles.addBtn} onClick={() => handleAddField("experience")}>+ Add Experience</button>

          <h3 style={styles.sectionTitle}>Certifications</h3>
          {formData.certifications.map((c, i) => (
            <div key={i} style={styles.dynamicField}>
              <input style={styles.input} placeholder="Certification" value={c} onChange={(e) => handleArrayChange("certifications", i, e.target.value)} />
              <button type="button" style={styles.removeBtn} onClick={() => handleRemoveField("certifications", i)}>✕</button>
            </div>
          ))}
          <button type="button" style={styles.addBtn} onClick={() => handleAddField("certifications")}>+ Add Certification</button>
        </>
      )}

      <h3 style={styles.sectionTitle}>Achievements</h3>
      {formData.achievements.map((a, i) => (
        <div key={i} style={styles.dynamicField}>
          <input style={styles.input} placeholder="Achievement" value={a} onChange={(e) => handleArrayChange("achievements", i, e.target.value)} />
          <button type="button" style={styles.removeBtn} onClick={() => handleRemoveField("achievements", i)}>✕</button>
        </div>
      ))}
      <button type="button" style={styles.addBtn} onClick={() => handleAddField("achievements")}>+ Add Achievement</button>
    </form>
  );
}

