export default function App() {
  const [activeSection, setActiveSection] = useState("fresher");
  const [formData, setFormData] = useState({
    name: "", email: "", phone: "", summary: "", education: "",
    skills: [""], projects: [""], experience: [""], certifications: [""], achievements: [""],
  });

  const downloadPDF = () => {
    const input = document.getElementById("resume-preview");
    html2canvas(input, { scale: 2 }).then((canvas) => {
      const pdf = new jsPDF("p", "mm", "a4");
      const imgData = canvas.toDataURL("image/png");
      const imgWidth = 210;
      const imgHeight = (canvas.height * imgWidth) / canvas.width;
      pdf.addImage(imgData, "PNG", 0, 0, imgWidth, imgHeight);
      pdf.save("resume.pdf");
    });
  };

  return (
    <div style={styles.app}>
      <header style={styles.header}>
        <h1 style={styles.headerH1}>Resume Builder</h1>
        <div style={styles.toggleContainer}>
          <button style={{...styles.toggleBtn, ...(activeSection==="fresher"?styles.toggleActive:{})}} onClick={() => setActiveSection("fresher")}>Fresher</button>
          <button style={{...styles.toggleBtn, ...(activeSection==="experienced"?styles.toggleActive:{})}} onClick={() => setActiveSection("experienced")}>Experienced</button>
        </div>
      </header>

      <main style={styles.mainContent}>
        <section style={styles.card}><Form formData={formData} setFormData={setFormData} activeSection={activeSection} /></section>
        <section style={styles.card}>
          <div style={styles.previewHeader}>
            <h2>Preview</h2>
            <button style={styles.downloadBtn} onClick={downloadPDF}>Download PDF</button>
          </div>
          <div id="resume-preview"><ResumePreview formData={formData} activeSection={activeSection} /></div>
        </section>
      </main>
    </div>
  );
}

