import React, { useState } from 'react';
import Form from './components/Form';
import ResumePreview from './components/ResumePreview';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import './App.css';

function App() {
  const [resumeType, setResumeType] = useState('fresher');
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    summary: '',
    education: [''],
    skills: '',
    projects: [''],
    experience: [''],
    achievements: ['']
  });

  const downloadPDF = () => {
    const input = document.getElementById('resume-preview');
    html2canvas(input).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgWidth = 210;
      const imgHeight = (canvas.height * imgWidth) / canvas.width;
      pdf.addImage(imgData, 'PNG', 0, 0, imgWidth, imgHeight);
      pdf.save('resume.pdf');
    });
  };

  return (
    <div className="App">
      <h1>Resume Builder</h1>
      <div className="resume-type">
        <button onClick={() => setResumeType('fresher')}>Fresher</button>
        <button onClick={() => setResumeType('experienced')}>Experienced</button>
      </div>
      <div className="main-container">
        <Form formData={formData} setFormData={setFormData} resumeType={resumeType} />
        <ResumePreview formData={formData} resumeType={resumeType} />
      </div>
      <button className="download-btn" onClick={downloadPDF}>Download PDF</button>
    </div>
  );
}

export default App;
