import React, { useState } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

// Form Component
const Form = ({ type, formData, setFormData }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleArrayChange = (section, index, field, value) => {
    setFormData(prev => {
      const newArray = [...prev[section]];
      newArray[index] = { ...newArray[index], [field]: value };
      return { ...prev, [section]: newArray };
    });
  };

  const addItem = (section) => {
    const templates = {
      education: { degree: '', school: '', year: '', gpa: '' },
      projects: { title: '', description: '', technologies: '', link: '' },
      experience: { jobTitle: '', company: '', years: '', location: '', description: '' },
      achievements: { text: '' },
      certifications: { name: '', issuer: '', year: '' }
    };
    setFormData(prev => ({
      ...prev,
      [section]: [...prev[section], templates[section]]
    }));
  };

  const removeItem = (section, index) => {
    setFormData(prev => ({
      ...prev,
      [section]: prev[section].filter((_, i) => i !== index)
    }));
  };

  return (
    <div style={styles.formContainer}>
      {/* Personal Information */}
      <div style={styles.formSection}>
        <div style={styles.sectionHeader}>
          <span style={styles.sectionIcon}></span>
          <h3 style={styles.sectionTitle}>Personal Information</h3>
        </div>
        <div style={styles.formGrid}>
          <div style={styles.formField}>
            <label style={styles.label}>Full Name *</label>
            <input
              type="text"
              name="fullName"
              placeholder="John Doe"
              value={formData.fullName}
              onChange={handleChange}
              style={styles.input}
            />
          </div>
          <div style={styles.formField}>
            <label style={styles.label}>Email Address *</label>
            <input
              type="email"
              name="email"
              placeholder="john.doe@email.com"
              value={formData.email}
              onChange={handleChange}
              style={styles.input}
            />
          </div>
          <div style={styles.formField}>
            <label style={styles.label}>Phone Number *</label>
            <input
              type="tel"
              name="phone"
              placeholder="+1 (555) 123-4567"
              value={formData.phone}
              onChange={handleChange}
              style={styles.input}
            />
          </div>
          <div style={styles.formField}>
            <label style={styles.label}>Location</label>
            <input
              type="text"
              name="location"
              placeholder="City, Country"
              value={formData.location}
              onChange={handleChange}
              style={styles.input}
            />
          </div>
          <div style={styles.formField}>
            <label style={styles.label}>LinkedIn Profile</label>
            <input
              type="url"
              name="linkedin"
              placeholder="linkedin.com/in/johndoe"
              value={formData.linkedin}
              onChange={handleChange}
              style={styles.input}
            />
          </div>
          <div style={styles.formField}>
            <label style={styles.label}>Portfolio/Website</label>
            <input
              type="url"
              name="portfolio"
              placeholder="johndoe.com"
              value={formData.portfolio}
              onChange={handleChange}
              style={styles.input}
            />
          </div>
        </div>
      </div>

      {/* Professional Summary */}
      <div style={styles.formSection}>
        <div style={styles.sectionHeader}>
          <span style={styles.sectionIcon}></span>
          <h3 style={styles.sectionTitle}>Professional Summary</h3>
        </div>
        <div style={styles.formField}>
          <label style={styles.label}>Summary</label>
          <textarea
            name="summary"
            placeholder="Write a brief professional summary highlighting your key skills and career objectives..."
            value={formData.summary}
            onChange={handleChange}
            rows="4"
            style={{...styles.input, minHeight: '100px', resize: 'vertical'}}
          />
        </div>
      </div>

      {/* Education */}
      <div style={styles.formSection}>
        <div style={styles.sectionHeader}>
          <span style={styles.sectionIcon}></span>
          <h3 style={styles.sectionTitle}>Education</h3>
        </div>
        {formData.education.map((edu, index) => (
          <div key={index} style={styles.arrayItem}>
            <div style={styles.arrayItemHeader}>
              <span style={styles.itemNumber}>#{index + 1}</span>
              {formData.education.length > 1 && (
                <button 
                  type="button" 
                  onClick={() => removeItem('education', index)} 
                  style={styles.removeBtnSmall}
                >

                </button>
              )}
            </div>
            <div style={styles.formGrid}>
              <div style={{...styles.formField, gridColumn: '1 / -1'}}>
                <label style={styles.label}>Degree/Qualification</label>
                <input
                  type="text"
                  placeholder="B.Tech in Computer Science"
                  value={edu.degree}
                  onChange={(e) => handleArrayChange('education', index, 'degree', e.target.value)}
                  style={styles.input}
                />
              </div>
              <div style={styles.formField}>
                <label style={styles.label}>School/University</label>
                <input
                  type="text"
                  placeholder="MIT"
                  value={edu.school}
                  onChange={(e) => handleArrayChange('education', index, 'school', e.target.value)}
                  style={styles.input}
                />
              </div>
              <div style={styles.formField}>
                <label style={styles.label}>Year</label>
                <input
                  type="text"
                  placeholder="2020 - 2024"
                  value={edu.year}
                  onChange={(e) => handleArrayChange('education', index, 'year', e.target.value)}
                  style={styles.input}
                />
              </div>
              <div style={styles.formField}>
                <label style={styles.label}>GPA/Percentage</label>
                <input
                  type="text"
                  placeholder="3.8/4.0 or 85%"
                  value={edu.gpa}
                  onChange={(e) => handleArrayChange('education', index, 'gpa', e.target.value)}
                  style={styles.input}
                />
              </div>
            </div>
          </div>
        ))}
        <button 
          type="button" 
          onClick={() => addItem('education')} 
          style={styles.addBtn}
        >
          + Add Education
        </button>
      </div>

      {/* Skills */}
      <div style={styles.formSection}>
        <div style={styles.sectionHeader}>
          <span style={styles.sectionIcon}></span>
          <h3 style={styles.sectionTitle}>Skills</h3>
        </div>
        <div style={styles.formField}>
          <label style={styles.label}>Technical Skills (comma-separated)</label>
          <textarea
            name="skills"
            placeholder="JavaScript, React, Node.js, Python, SQL, Git, AWS, Docker"
            value={formData.skills}
            onChange={handleChange}
            rows="3"
            style={{...styles.input, minHeight: '80px', resize: 'vertical'}}
          />
          <small style={styles.fieldHint}>Separate each skill with a comma</small>
        </div>
      </div>

      {/* Projects (Fresher) */}
      {type === 'fresher' && (
        <div style={styles.formSection}>
          <div style={styles.sectionHeader}>
            <span style={styles.sectionIcon}></span>
            <h3 style={styles.sectionTitle}>Projects</h3>
          </div>
          {formData.projects.map((project, index) => (
            <div key={index} style={styles.arrayItem}>
              <div style={styles.arrayItemHeader}>
                <span style={styles.itemNumber}>#{index + 1}</span>
                {formData.projects.length > 1 && (
                  <button 
                    type="button" 
                    onClick={() => removeItem('projects', index)} 
                    style={styles.removeBtnSmall}
                  >

                  </button>
                )}
              </div>
              <div style={styles.formGrid}>
                <div style={styles.formField}>
                  <label style={styles.label}>Project Title</label>
                  <input
                    type="text"
                    placeholder="E-commerce Platform"
                    value={project.title}
                    onChange={(e) => handleArrayChange('projects', index, 'title', e.target.value)}
                    style={styles.input}
                  />
                </div>
                <div style={styles.formField}>
                  <label style={styles.label}>Technologies Used</label>
                  <input
                    type="text"
                    placeholder="React, Node.js, MongoDB"
                    value={project.technologies}
                    onChange={(e) => handleArrayChange('projects', index, 'technologies', e.target.value)}
                    style={styles.input}
                  />
                </div>
                <div style={{...styles.formField, gridColumn: '1 / -1'}}>
                  <label style={styles.label}>Project Link</label>
                  <input
                    type="url"
                    placeholder="https://github.com/johndoe/project"
                    value={project.link}
                    onChange={(e) => handleArrayChange('projects', index, 'link', e.target.value)}
                    style={styles.input}
                  />
                </div>
                <div style={{...styles.formField, gridColumn: '1 / -1'}}>
                  <label style={styles.label}>Description</label>
                  <textarea
                    placeholder="Describe your project, key features, and your role..."
                    value={project.description}
                    onChange={(e) => handleArrayChange('projects', index, 'description', e.target.value)}
                    rows="3"
                    style={{...styles.input, minHeight: '80px', resize: 'vertical'}}
                  />
                </div>
              </div>
            </div>
          ))}
          <button 
            type="button" 
            onClick={() => addItem('projects')} 
            style={styles.addBtn}
          >
            + Add Project
          </button>
        </div>
      )}

      {/* Experience (Experienced) */}
      {type === 'experienced' && (
        <>
          <div style={styles.formSection}>
            <div style={styles.sectionHeader}>
              <span style={styles.sectionIcon}></span>
              <h3 style={styles.sectionTitle}>Work Experience</h3>
            </div>
            {formData.experience.map((exp, index) => (
              <div key={index} style={styles.arrayItem}>
                <div style={styles.arrayItemHeader}>
                  <span style={styles.itemNumber}>#{index + 1}</span>
                  {formData.experience.length > 1 && (
                    <button 
                      type="button" 
                      onClick={() => removeItem('experience', index)} 
                      style={styles.removeBtnSmall}
                    >

                    </button>
                  )}
                </div>
                <div style={styles.formGrid}>
                  <div style={styles.formField}>
                    <label style={styles.label}>Job Title</label>
                    <input
                      type="text"
                      placeholder="Senior Software Engineer"
                      value={exp.jobTitle}
                      onChange={(e) => handleArrayChange('experience', index, 'jobTitle', e.target.value)}
                      style={styles.input}
                    />
                  </div>
                  <div style={styles.formField}>
                    <label style={styles.label}>Company Name</label>
                    <input
                      type="text"
                      placeholder="Tech Corp Inc."
                      value={exp.company}
                      onChange={(e) => handleArrayChange('experience', index, 'company', e.target.value)}
                      style={styles.input}
                    />
                  </div>
                  <div style={styles.formField}>
                    <label style={styles.label}>Duration</label>
                    <input
                      type="text"
                      placeholder="Jan 2020 - Present"
                      value={exp.years}
                      onChange={(e) => handleArrayChange('experience', index, 'years', e.target.value)}
                      style={styles.input}
                    />
                  </div>
                  <div style={styles.formField}>
                    <label style={styles.label}>Location</label>
                    <input
                      type="text"
                      placeholder="San Francisco, CA"
                      value={exp.location}
                      onChange={(e) => handleArrayChange('experience', index, 'location', e.target.value)}
                      style={styles.input}
                    />
                  </div>
                  <div style={{...styles.formField, gridColumn: '1 / -1'}}>
                    <label style={styles.label}>Job Description & Achievements</label>
                    <textarea
                      placeholder=" Led a team of 5 developers&#10; Improved system performance by 40%&#10; Implemented CI/CD pipeline"
                      value={exp.description}
                      onChange={(e) => handleArrayChange('experience', index, 'description', e.target.value)}
                      rows="4"
                      style={{...styles.input, minHeight: '100px', resize: 'vertical'}}
                    />
                  </div>
                </div>
              </div>
            ))}
            <button 
              type="button" 
              onClick={() => addItem('experience')} 
              style={styles.addBtn}
            >
              + Add Experience
            </button>
          </div>

          <div style={styles.formSection}>
            <div style={styles.sectionHeader}>
              <span style={styles.sectionIcon}></span>
              <h3 style={styles.sectionTitle}>Certifications</h3>
            </div>
            {formData.certifications.map((cert, index) => (
              <div key={index} style={styles.arrayItem}>
                <div style={styles.arrayItemHeader}>
                  <span style={styles.itemNumber}>#{index + 1}</span>
                  {formData.certifications.length > 1 && (
                    <button 
                      type="button" 
                      onClick={() => removeItem('certifications', index)} 
                      style={styles.removeBtnSmall}
                    >

                    </button>
                  )}
                </div>
                <div style={styles.formGrid}>
                  <div style={styles.formField}>
                    <label style={styles.label}>Certification Name</label>
                    <input
                      type="text"
                      placeholder="AWS Certified Solutions Architect"
                      value={cert.name}
                      onChange={(e) => handleArrayChange('certifications', index, 'name', e.target.value)}
                      style={styles.input}
                    />
                  </div>
                  <div style={styles.formField}>
                    <label style={styles.label}>Issuing Organization</label>
                    <input
                      type="text"
                      placeholder="Amazon Web Services"
                      value={cert.issuer}
                      onChange={(e) => handleArrayChange('certifications', index, 'issuer', e.target.value)}
                      style={styles.input}
                    />
                  </div>
                  <div style={styles.formField}>
                    <label style={styles.label}>Year</label>
                    <input
                      type="text"
                      placeholder="2023"
                      value={cert.year}
                      onChange={(e) => handleArrayChange('certifications', index, 'year', e.target.value)}
                      style={styles.input}
                    />
                  </div>
                </div>
              </div>
            ))}
            <button 
              type="button" 
              onClick={() => addItem('certifications')} 
              style={styles.addBtn}
            >
              + Add Certification
            </button>
          </div>

          <div style={styles.formSection}>
            <div style={styles.sectionHeader}>
              <span style={styles.sectionIcon}></span>
              <h3 style={styles.sectionTitle}>Achievements & Awards</h3>
            </div>
            {formData.achievements.map((achievement, index) => (
              <div key={index} style={styles.arrayItem}>
                <div style={styles.arrayItemHeader}>
                  <span style={styles.itemNumber}>#{index + 1}</span>
                  {formData.achievements.length > 1 && (
                    <button 
                      type="button" 
                      onClick={() => removeItem('achievements', index)} 
                      style={styles.removeBtnSmall}
                    >

                    </button>
                  )}
                </div>
                <div style={{...styles.formField, gridColumn: '1 / -1'}}>
                  <label style={styles.label}>Achievement</label>
                  <textarea
                    placeholder="Received Employee of the Year award for outstanding performance"
                    value={achievement.text}
                    onChange={(e) => handleArrayChange('achievements', index, 'text', e.target.value)}
                    rows="2"
                    style={{...styles.input, minHeight: '60px', resize: 'vertical'}}
                  />
                </div>
              </div>
            ))}
            <button 
              type="button" 
              onClick={() => addItem('achievements')} 
              style={styles.addBtn}
            >
              + Add Achievement
            </button>
          </div>
        </>
      )}
    </div>
  );
};

// Resume Preview Component
const ResumePreview = ({ type, data }) => {
  const hasContent = (arr) => arr && arr.length > 0 && arr.some(item => 
    Object.values(item).some(val => val && val.trim() !== '')
  );

  const formatDescription = (text) => {
    if (!text) return null;
    return text.split('\n').map((line, index) => (
      line.trim() && <div key={index} style={styles.descriptionLine}>{line}</div>
    ));
  };

  return (
    <div id="resume-preview" style={styles.resumePreview}>
      {/* Header Section */}
      <div style={styles.resumeHeader}>
        <h1 style={styles.resumeName}>{data.fullName || 'YOUR NAME'}</h1>

        <div style={styles.resumeContactInfo}>
          {data.email && (
            <div style={styles.contactItem}>
              <span style={styles.contactIcon}></span>
              <span>{data.email}</span>
            </div>
          )}
          {data.phone && (
            <div style={styles.contactItem}>
              <span style={styles.contactIcon}></span>
              <span>{data.phone}</span>
            </div>
          )}
          {data.location && (
            <div style={styles.contactItem}>
              <span style={styles.contactIcon}></span>
              <span>{data.location}</span>
            </div>
          )}
          {data.linkedin && (
            <div style={styles.contactItem}>
              <span style={styles.contactIcon}></span>
              <span style={styles.linkText}>{data.linkedin.replace(/^https?:\/\//, '')}</span>
            </div>
          )}
          {data.portfolio && (
            <div style={styles.contactItem}>
              <span style={styles.contactIcon}></span>
              <span style={styles.linkText}>{data.portfolio.replace(/^https?:\/\//, '')}</span>
            </div>
          )}
        </div>
      </div>

      {/* Professional Summary */}
      {data.summary && data.summary.trim() && (
        <div style={styles.resumeSection}>
          <h2 style={styles.resumeSectionTitle}>
            <span style={styles.titleLine}></span>
            Professional Summary
          </h2>
          <div style={styles.sectionContent}>
            <p style={styles.summaryText}>{data.summary}</p>
          </div>
        </div>
      )}

      {/* Education Section */}
      {hasContent(data.education) && (
        <div style={styles.resumeSection}>
          <h2 style={styles.resumeSectionTitle}>
            <span style={styles.titleLine}></span>
            Education
          </h2>
          <div style={styles.sectionContent}>
            {data.education.map((edu, index) => (
              (edu.degree || edu.school) && (
                <div key={index} style={styles.contentItem}>
                  <div style={styles.itemHeaderRow}>
                    <div style={styles.itemLeft}>
                      <h3 style={styles.itemTitle}>{edu.degree || 'Degree'}</h3>
                      <div style={styles.itemSubtitle}>{edu.school}</div>
                    </div>
                    <div style={styles.itemRight}>
                      {edu.year && <div style={styles.itemDate}>{edu.year}</div>}
                      {edu.gpa && <div style={styles.itemGpa}>GPA: {edu.gpa}</div>}
                    </div>
                  </div>
                </div>
              )
            ))}
          </div>
        </div>
      )}

      {/* Skills Section */}
      {data.skills && data.skills.trim() && (
        <div style={styles.resumeSection}>
          <h2 style={styles.resumeSectionTitle}>
            <span style={styles.titleLine}></span>
            Technical Skills
          </h2>
          <div style={styles.sectionContent}>
            <div style={styles.skillsGrid}>
              {data.skills.split(',').map((skill, index) => (
                skill.trim() && (
                  <span key={index} style={styles.skillBadge}>
                    {skill.trim()}
                  </span>
                )
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Work Experience (Experienced) */}
      {type === 'experienced' && hasContent(data.experience) && (
        <div style={styles.resumeSection}>
          <h2 style={styles.resumeSectionTitle}>
            <span style={styles.titleLine}></span>
            Professional Experience
          </h2>
          <div style={styles.sectionContent}>
            {data.experience.map((exp, index) => (
              (exp.jobTitle || exp.company) && (
                <div key={index} style={styles.contentItem}>
                  <div style={styles.itemHeaderRow}>
                    <div style={styles.itemLeft}>
                      <h3 style={styles.itemTitle}>{exp.jobTitle || 'Position'}</h3>
                      <div style={styles.itemSubtitle}>
                        {exp.company}
                        {exp.location && <span style={styles.locationBadge}>{exp.location}</span>}
                      </div>
                    </div>
                    <div style={styles.itemRight}>
                      {exp.years && <div style={styles.itemDate}>{exp.years}</div>}
                    </div>
                  </div>
                  {exp.description && (
                    <div style={styles.itemDescription}>
                      {formatDescription(exp.description)}
                    </div>
                  )}
                </div>
              )
            ))}
          </div>
        </div>
      )}

      {/* Projects (Fresher) */}
      {type === 'fresher' && hasContent(data.projects) && (
        <div style={styles.resumeSection}>
          <h2 style={styles.resumeSectionTitle}>
            <span style={styles.titleLine}></span>
            Projects
          </h2>
          <div style={styles.sectionContent}>
            {data.projects.map((project, index) => (
              (project.title || project.description) && (
                <div key={index} style={styles.contentItem}>
                  <div style={styles.itemHeaderRow}>
                    <div style={styles.itemLeft}>
                      <h3 style={styles.itemTitle}>{project.title || 'Project'}</h3>
                      {project.technologies && (
                        <div style={styles.itemSubtitle}>{project.technologies}</div>
                      )}
                    </div>
                    <div style={styles.itemRight}>
                      {project.link && (
                        <div style={styles.projectLink}> {project.link.replace(/^https?:\/\//, '').substring(0, 30)}</div>
                      )}
                    </div>
                  </div>
                  {project.description && (
                    <div style={styles.itemDescription}>
                      {formatDescription(project.description)}
                    </div>
                  )}
                </div>
              )
            ))}
          </div>
        </div>
      )}

      {/* Certifications (Experienced) */}
      {type === 'experienced' && hasContent(data.certifications) && (
        <div style={styles.resumeSection}>
          <h2 style={styles.resumeSectionTitle}>
            <span style={styles.titleLine}></span>
            Certifications
          </h2>
          <div style={styles.sectionContent}>
            {data.certifications.map((cert, index) => (
              (cert.name || cert.issuer) && (
                <div key={index} style={styles.certificationItem}>
                  <div style={styles.certBullet}></div>
                  <div style={styles.certContent}>
                    <strong>{cert.name}</strong>
                    {cert.issuer && <span> - {cert.issuer}</span>}
                    {cert.year && <span style={styles.certYear}> ({cert.year})</span>}
                  </div>
                </div>
              )
            ))}
          </div>
        </div>
      )}

      {/* Achievements (Experienced) */}
      {type === 'experienced' && hasContent(data.achievements) && (
        <div style={styles.resumeSection}>
          <h2 style={styles.resumeSectionTitle}>
            <span style={styles.titleLine}></span>
            Achievements & Awards
          </h2>
          <div style={styles.sectionContent}>
            {data.achievements.map((achievement, index) => (
              achievement.text && achievement.text.trim() && (
                <div key={index} style={styles.achievementItem}>
                  <div style={styles.achievementBullet}></div>
                  <div style={styles.achievementText}>{achievement.text}</div>
                </div>
              )
            ))}
          </div>
        </div>
      )}

      {/* Empty State */}
      {!data.fullName && !data.email && !data.phone && (
        <div style={styles.emptyState}>
          <div style={styles.emptyIcon}></div>
          <h3 style={{fontSize: '1.5rem', color: '#718096', marginBottom: '0.8rem', fontWeight: 700}}>Start Building Your Resume</h3>
          <p style={{fontSize: '1rem', color: '#a0aec0'}}>Fill in the form on the left to see your professional resume preview here</p>
        </div>
      )}
    </div>
  );
};

// Main App Component
function App() {
  const [resumeType, setResumeType] = useState('fresher');
  const [isDownloading, setIsDownloading] = useState(false);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    location: '',
    linkedin: '',
    portfolio: '',
    summary: '',
    education: [{ degree: '', school: '', year: '', gpa: '' }],
    skills: '',
    projects: [{ title: '', description: '', technologies: '', link: '' }],
    experience: [{ jobTitle: '', company: '', years: '', location: '', description: '' }],
    achievements: [{ text: '' }],
    certifications: [{ name: '', issuer: '', year: '' }]
  });

  const handleDownloadPDF = async () => {
    const element = document.getElementById('resume-preview');
    setIsDownloading(true);

    try {
      const canvas = await html2canvas(element, {
        scale: 3,
        useCORS: true,
        logging: false,
        backgroundColor: '#ffffff',
        windowWidth: 1200,
        windowHeight: element.scrollHeight
      });

      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');

      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();
      const imgWidth = canvas.width;
      const imgHeight = canvas.height;
      const ratio = Math.min(pdfWidth / imgWidth, pdfHeight / imgHeight);
      const imgX = (pdfWidth - imgWidth * ratio) / 2;
      const imgY = 5;

      pdf.addImage(imgData, 'PNG', imgX, imgY, imgWidth * ratio, imgHeight * ratio);
      pdf.save(`${formData.fullName.replace(/\s+/g, '_') || 'resume'}_${new Date().getFullYear()}.pdf`);
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Failed to generate PDF. Please try again.');
    } finally {
      setIsDownloading(false);
    }
  };

  return (
    <div style={styles.app}>
      <header style={styles.header}>
        <div style={styles.headerContent}>
          <div style={styles.headerTitle}>
            <h1 style={styles.headerH1}> Professional Resume Builder</h1>
            <p style={styles.headerP}>Create a stunning resume in minutes</p>
          </div>
          <div style={styles.toggleContainer}>
            <button
              style={{
                ...styles.toggleBtn,
                ...(resumeType === 'fresher' ? styles.toggleBtnActive : {})
              }}
              onClick={() => setResumeType('fresher')}
            >
              <span style={{fontSize: '1.2rem'}}></span>
              Fresher
            </button>
            <button
              style={{
                ...styles.toggleBtn,
                ...(resumeType === 'experienced' ? styles.toggleBtnActive : {})
              }}
              onClick={() => setResumeType('experienced')}
            >
              <span style={{fontSize: '1.2rem'}}></span>
              Experienced
            </button>
          </div>
        </div>
      </header>

      <div style={styles.mainContent}>
        <div style={styles.formSectionWrapper}>
          <div style={styles.formHeader}>
            <h2 style={styles.formHeaderH2}> Build Your Resume</h2>
            <p style={styles.formHeaderP}>Fill in your details to create a professional resume</p>
          </div>
          <Form 
            type={resumeType} 
            formData={formData} 
            setFormData={setFormData} 
          />
        </div>

        <div style={styles.previewSectionWrapper}>
          <div style={styles.previewHeader}>
            <div>
              <h2 style={styles.previewHeaderH2}> Live Preview</h2>
              <p style={styles.previewHeaderP}>Your resume updates in real-time</p>
            </div>
            <button 
              style={{
                ...styles.downloadBtn,
                ...(isDownloading ? {opacity: 0.6, cursor: 'not-allowed'} : {})
              }}
              onClick={handleDownloadPDF}
              disabled={isDownloading}
            >
              {isDownloading ? ' Generating...' : ' Download PDF'}
            </button>
          </div>
          <div style={styles.previewContainer}>
            <ResumePreview 
              type={resumeType} 
              data={formData} 
            />
          </div>
        </div>
      </div>
    </div>
  );
}

